Shader &#34;URP/HalfLambertBlinnPhong&#34; { Properties { _Diffuse (&#34;Diffuse&#34;, Color) = (1,1,1,1) _Specular(&#34;Specular&#34;,Color) = (1,1,1,1) _Gloss(&#34;Gloss&#34;,Range(1.0,200))=10 } SubShader { Tags { &#34;RenderPipeline&#34;=&#34;UniversalRenderPipeline&#34; &#34;RenderType&#34;=&#34;Opaque&#34; } Pass { Tags { &#34;LightMode&#34;=&#34;UniversalForward&#34;} HLSLPROGRAM // UnityCG.cginc æ–‡ä»¶è¢«ä»£æ›¿ï¼Œä½¿ç”¨ HLSL æ–‡ä»¶ï¼Œè¿™é‡Œå¼•ç”¨æ–‡ä»¶ #include &#34;Packages/com.unity.render-pipelines.universal/ShaderLibrary/Core.hlsl&#34; #include &#34;Packages/com.unity.render-pipelines.universal/ShaderLibrary/Lighting.hlsl&#34; #include &#34;Packages/com.unity.render-pipelines.core/ShaderLibrary/SpaceTransforms.hlsl&#34; #pragma vertex vert #pragma fragment frag CBUFFER_START(UnityPerMaterial) // å…¼å®¹ SRP æ‰¹å¤„ç†ï¼Œå°†æ‰€æœ‰æè´¨å±æ€§å£°æ˜åœ¨ä¸€ä¸ª UnityPerMaterial çš„ CBUFFER å—ä¸­  float4 _Diffuse; float4 _Specular; float _Gloss; CBUFFER_END struct a2v { float4 vertex : POSITION; float3 normal : NORMAL; float2 lightmapUV : TEXCOORD1; }; struct v2f { float4 pos : SV_POSITION; float3 worldNormal : TEXCOORD0; float3 worldPos : TEXCOORD1; }; v2f vert(a2v v) { v2f o; ZERO_INITIALIZE(v2f, o); // åˆå§‹åŒ–å˜é‡ o.pos=TransformObjectToHClip(v.vertex.xyz); // é¡¶ç‚¹åæ ‡ä»æ¨¡å‹ç©ºé—´è½¬åˆ°è£å‰ªç©ºé—´ o.worldNormal = TransformObjectToWorldDir(v.normal); // æ³•çº¿ä»æ¨¡å‹ç©ºé—´è½¬åˆ°ä¸–ç•Œç©ºé—´ o.worldPos = TransformObjectToWorldDir(v.vertex.xyz); // é¡¶ç‚¹ä»å¯¹è±¡ç©ºé—´è½¬åˆ°ä¸–ç•Œç©ºé—´ return o; } half4 frag(v2f i):SV_Target { Light mainLight = GetMainLight(); // è·å–ä¸»å…‰æº half3 worldNormal = normalize(i.worldNormal); // é¡¶ç‚¹æ³•çº¿æ–¹å‘å½’ä¸€åŒ– half3 worldLightDir = normalize(TransformObjectToWorldDir(mainLight.direction)); // ä¸»å…‰æºæ–¹å‘å½’ä¸€åŒ– half3 viewDir = normalize(_WorldSpaceCameraPos.xyz - i.worldPos); // è®¡ç®—è§†è§’æ–¹å‘å¹¶ä¸”å½’ä¸€åŒ– half3 lightReflectDir = reflect( -worldLightDir, worldNormal ); // è®¡ç®— phong æ¨¡å‹ä¸­é«˜å…‰éƒ¨åˆ† half3 halfDir = normalize(worldLightDir + viewDir); // è®¡ç®—åŠè§’æ–¹å‘ä¸”å½’ä¸€åŒ– half3 NdotL = max(0.0, dot(worldNormal, worldLightDir)); // Lambert half3 RdotV = max(0.0, dot(worldNormal, lightReflectDir)); // Phong half3 NdotV = max(0.0, dot(worldNormal, halfDir)); // Blinn-Phong /// lambert + Phong //half3 diffuse = mainLight.color.rgb * _Diffuse.rgb * NdotL; //half3 specular = mainLight.color.rgb * _Specular.rgb * pow(RdotV, _Gloss); /// Lambert + Blinn-Phong //half3 diffuse = mainLight.color.rgb * _Diffuse.rgb * NdotL; //half3 specular = mainLight.color.rgb * _Specular.rgb * pow(NdotV, _Gloss); /// Half Lambert + Phong //half3 diffuse = mainLight.color.rgb * _Diffuse.rgb * pow(NdotL * 0.5 + 0.5, 2.0); //half3 specular = mainLight.color.rgb * _Specular.rgb * pow(RdotV, _Gloss); /// Half Lambert + Blinn-Phong half3 diffuse = mainLight.color.rgb * _Diffuse.rgb * pow(NdotL * 0.5 + 0.5, 2.0); half3 specular = mainLight.color.rgb * _Specular.rgb * pow(NdotV, _Gloss); // è®¡ç®—ç¯å¢ƒå…‰ half3 ambient = _GlossyEnvironmentColor; half4 col= half4(ambient + diffuse + specular, 1.0); return col; } ENDHLSL } } FallBack &#34;Packages/com.unity.render-pipelines.universal/FallbackError&#34; } Shader &#34;SRP/HalfLambertBlinnPhong&#34; { Properties { _Diffuse(&#34;Diffuse Color&#34;, Color) = (1,1,1,1) _SpcularColor(&#34;Spcular Color&#34;, Color) = (1,1,1,1) _SpcularStrength(&#34;Spcular Strenght&#34;, Range(0.0, 100)) = 10 } SubShader { Tags { &#34;RenderType&#34;=&#34;Opaque&#34; } LOD 100 Pass { CGPROGRAM #pragma vertex vert #pragma fragment frag #include &#34;UnityCG.cginc&#34; #include &#34;Lighting.cginc&#34; float4 _Diffuse; float4 _SpcularColor; float _SpcularStrength; struct appdata { float4 vertex : POSITION; float3 normal : NORMAL; }; struct v2f { float4 pos : SV_POSITION; float3 worldNormal : NORMAL; float3 worldPos : TEXCOORD0; }; v2f vert (appdata v) { v2f o; o.pos = UnityObjectToClipPos(v.vertex); // é¡¶ç‚¹åæ ‡ä»æ¨¡å‹ç©ºé—´è½¬åˆ°è£å‰ªç©ºé—´ o.worldNormal = UnityObjectToWorldDir(v.normal); // å°†æ³•çº¿ä»æ¨¡å‹ç©ºé—´è½¬åˆ°ä¸–ç•Œç©ºé—´ o.worldPos = UnityObjectToWorldDir(v.vertex); // å°†é¡¶ç‚¹ä»æ¨¡å‹ç©ºé—´è½¬åˆ°ä¸–ç•Œç©ºé—´ return o; } fixed4 frag (v2f i) : SV_Target { half3 ambient = UNITY_LIGHTMODEL_AMBIENT.xyz; // è·å–ç¯å¢ƒå…‰ half3 worldLightDir=normalize(_WorldSpaceLightPos0.xyz); // ä¸»å…‰æºçš„æ–¹å‘ half3 worldNormal = normalize(i.worldNormal); // æ³•çº¿æ–¹å‘ half3 viewDir = normalize(_WorldSpaceCameraPos.xyz - i.worldPos); // è§†è§’æ–¹å‘ half3 lightReflectDir = reflect(-worldLightDir, worldNormal); // ä¸»å…‰æºå…‰çº¿åå°„æ–¹å‘ half3 halfDir = normalize(viewDir + worldLightDir); half3 NdotL = max(0.0, dot(worldNormal, worldLightDir)); // lambert half3 RdotV = max(0.0, dot(worldNormal, lightReflectDir)); // phong half3 NdotV = max(0.0, dot(worldNormal, halfDir)); // blinn phong // lambert + phong //half3 diffuse = _LightColor0.rgb * _Diffuse * NdotL; //half3 specular = _LightColor0.rgb * _SpcularColor.rgb * pow(RdotV, _SpcularStrength); // Lambert + blinn phong //half3 diffuse = _LightColor0.rgb * _Diffuse * NdotL; //half3 specular = _LightColor0.rgb * _SpcularColor.rgb * pow(NdotV, _SpcularStrength); // half lambert + phong //half3 diffuse = _LightColor0.rgb * _Diffuse * pow(NdotL * 0.5 + 0.5, 2.0); //half3 specular = _LightColor0.rgb * _SpcularColor.rgb * pow(RdotV, _SpcularStrength); // half lambert + blinn phong half3 diffuse = _LightColor0.rgb * _Diffuse * pow(NdotL * 0.5 + 0.5, 2.0); half3 specular = _LightColor0.rgb * _SpcularColor.rgb * pow(NdotV, _SpcularStrength); return half4(diffuse + specular + ambient, 1); } ENDCG } } }
